test('Initial setup', function tInitialSetup(suite) {
    h.reset();

    suite.test('Create client and server', function tCreateClientServer(t) {
        h.createClientAndServer(function (err, client, moray, server) {
            KBMAPI = server;
            MORAY = moray;
            CLIENT = client;
            t.ifError(err, 'server creation');
            t.ok(KBMAPI, 'server');
            t.ok(MORAY, 'moray');
            t.ok(CLIENT, 'client');
            t.end();
        });
    });

    suite.test('Create token w/o cn_uuid', function tCreateWOCNUuid(t) {
        var tk = mod_jsprim.deepCopy(TOKENS[0]);
        delete tk.cn_uuid;
        mod_token.create(t, {
            params: tk,
            expErr: {
                code: 'InvalidParameters',
                message: 'Missing parameters',
                errors: [ {
                    field: 'cn_uuid',
                    code: 'MissingParameter',
                    message: 'Missing parameter'
                } ]
            },
            expCode: 422
        });
    });

    suite.test('Create token w/o pin', function tCreateWOPin(t) {
        var tk = mod_jsprim.deepCopy(TOKENS[0]);
        delete tk.pin;
        mod_token.create(t, {
            params: tk,
            expErr: {
                code: 'InvalidParameters',
                message: 'Missing parameters',
                errors: [ {
                    field: 'pin',
                    code: 'MissingParameter',
                    message: 'Missing parameter'
                } ]
            },
            expCode: 422
        });
    });

    suite.test('Create token w/o pubkeys', function tCreateWOPubkeys(t) {
        var tk = mod_jsprim.deepCopy(TOKENS[0]);
        delete tk.pubkeys;
        mod_token.create(t, {
            params: tk,
            expErr: {
                code: 'InvalidParameters',
                message: 'Missing parameters',
                errors: [ {
                    field: 'pubkeys',
                    code: 'MissingParameter',
                    message: 'Missing parameter'
                } ]
            },
            expCode: 422
        });
    });

    suite.test('Create token invalid pubkeys', function tCreateInvalidKeys(t) {
        var tk = mod_jsprim.deepCopy(TOKENS[0]);
        tk.pubkeys['9e'] = null;
        mod_token.create(t, {
            params: tk,
            expErr: {
                code: 'InvalidParameters',
                message: 'Invalid parameters',
                errors: [ {
                    field: 'pubkeys.9e',
                    code: 'InvalidParameter',
                    message: 'must be a string'
                } ]
            },
            expCode: 422
        });
    });

    suite.test('Create tokens', function tCreateTokens(t) {
        TOKENS.forEach(function eachToken(token) {
            t.test('Create token GUID ' + token.guid, function doCreate(t3) {
                mod_token.create(t3, {
                    params: token,
                    exp: token
                });
            });
        });
        t.end();
    });

    suite.test('Get token', function tGetToken(t) {
        var tok = TOKENS[0];

        mod_token.get(t, {
            params: {
                guid: tok.guid
            },
            exp: {
                guid: tok.guid,
                serial: tok.serial,
                cn_uuid: tok.cn_uuid,
                model: tok.model,
                pubkeys: tok.pubkeys,
                attestation: tok.attestation
            },
            fillIn: ['recovery_tokens']
        });
    });

    suite.test('Get token with pin', function tGetTokenPin(t) {
        var tok = TOKENS[0];

        mod_token.getPin(t, {
            params: {
                guid: tok.guid
            },
            exp: tok
        });
    });

    suite.test('List tokens', function tListTokens(t) {
        var tokens = mod_jsprim.deepCopy(TOKENS);

        tokens.forEach(function stripPin(tok) {
            delete tok.pin;
        });

        mod_token.list(t, {
            params: {},
            deepEqual: true,
            present: tokens
            });
    });

    suite.test('Delete token', function tDeleteToken(t) {
        mod_token.delete(t, {
            params: {
                guid: TOKENS[0].guid
            },
            exp: {}
        });
    });

    suite.test('Lookup deleted token', function tGetDeletedToken(t) {
        mod_token.get(t, {
            params: {
                guid: TOKENS[0].guid
            },
            expCode: 404,
            expErr: {
                code: 'ResourceNotFound',
                message: 'piv tokens not found'
            }
        });
    });
});

test('Stop server', function closeServers(t) {
    KBMAPI.server.close();
    mod_server.close(t);
});
